<template>
  <li class="recommendedli">
    <router-link :to="'/Detail?id=' + item.id">
      <div class="imginfo">
        <img :src="item.picUrl" alt="" />
        <span>{{ NewNum }}</span>
      </div>
      <span>{{ item.name }}</span>
    </router-link>
  </li>
</template>

<script>
export default {
  props: {
    item: {
      type: Object,
    },
  },
  computed: {
    NewNum: function () {
      if (this.item.playCount > 99999999) {
        return (this.item.playCount / 100000000).toFixed(1) + "亿";
      } else if (this.item.playCount > 9999) {
        return (this.item.playCount / 10000).toFixed(1) + "万";
      } else {
        return this.item.playCount;
      }
    },
  },
};
</script>

